import React, { useState } from 'react';

// Add conditional for if they have 7 days of data entered

const Experiments = () => {

    const [experiment, setExperiment] = useState({
        date: '',
        endDate: '',
        experiment: ''
    });

    const handleSelect = e => {
        setExperiment({
            ...experiment,
            [e.target.name]: e.target.value,
            date: new Date().toISOString().substr(0, 10)
        })
    }

    console.log(experiment);

    // const handleSubmit = e => {
    //     e.preventDefault();
    //     props.addExperiment(setExperiemntSelect);
    //     props.history.push(`/sleep/${sleepText.date}`)
    // };

    return (
        <div>
            <h2>Create your experiment</h2>
            <p>Experiments are each one week long</p>

            <form>
                <input
                    type="radio"
                    value="alcohol"
                    id="alcohol"
                    onChange={handleSelect}
                    name="experiment"
                />
                <label for="alcohol">Drink no alcohol</label>

                <input
                    type="radio"
                    value="sleepearlier"
                    id="sleepearlier"
                    onChange={handleSelect}
                    name="experiment"
                />
                <label for="sleepearlier">Go to sleep one hour earlier</label>

                <input
                    type="radio"
                    value="temp"
                    id="temp"
                    onChange={handleSelect}
                    name="experiment"
                />
                <label for="temp">Set temperature to 65 degrees</label>

                <input
                    type="radio"
                    value="eat"
                    id="eat"
                    onChange={handleSelect}
                    name="experiment"
                />
                <label for="eat">Don't eat 3 hours before bed</label>

                <button>Create experiment</button>


            </form>
        </div>
    )
}

export default Experiments;
