import { ADD_SLEEP } from '../actions/sleepActions';

const initialState = {
    sleepEntries: [
        {
            duration_hours: 6.45,
            duration_minutes: 5,
            score: 5,
            bedtime: 10.25,
            date: '2019-12-28'
            // Date will be handled by the server eventually
        }
    ]
}

export const sleepReducer = (state = initialState, action) => {
    console.log(state, action);
    switch (action.type) {
        case ADD_SLEEP:
            return {
                ...state,
                sleepEntries: [
                    ...state.sleepEntries,
                    action.payload
                ]
            }
        default:
            return state;
    }
}
